import { render } from 'react-dom'
import React, { useState, useCallback } from 'react'
import { useTransition, animated } from 'react-spring'
import './index.css'

const pages = [
  ({ style }) => <animated.div style={{ ...style, background: 'lightblue' }}>
  Want to Get an Awesome Resume?<br/>
  </animated.div>,
  ({ style }) => <animated.div style={{ ...style, background: 'lightblue' }}>Just Follow 3 Steps</animated.div>,
  ({ style }) => <animated.div style={{ ...style, background: 'lightgreen' }}>
    <button className="btn btn-primary" onClick="javascript:window.location='/base'">Link</button>
  </animated.div>,
]

export default function App() {
  const [index, set] = useState(0)
  const onClick = useCallback(() => set(state => (state + 1) % 3), [])
  const transitions = useTransition(index, p => p, {
    from: { opacity: 0.5, transform: 'translate3d(0,0,0)' },
    enter: { opacity: 1, transform: 'translate3d(0,0,0)' },
    leave: { opacity: 0.5, transform: 'translate3d(0,0,0)' },
  })
  return (
    <div className="simple-trans-main" onClick={onClick}>
      {transitions.map(({ item, props, key }) => {
        const Page = pages[item]
        return <Page key={key} style={props} />
      })}
    </div>
  )
}

render(<App />, document.getElementById('root'))
