import React, { Component } from 'react';
import posed from "react-pose"
import Box from "./Box";
const Swipeable=posed.div({
    draggable:"x",
    
})




class Home extends Component {
    constructor(props) {
        super(props);
        this.state = { open: false }
    }
    handleOpen = e => {
        e.stopPropagation();
        this.setState({
          open: true
        });
      };
    
      handleClose = e => {
        this.setState({
          open: false
        });
      };
    render() { 
        const { open } = this.state;
        return ( 
            <div>
                <div>
                    <Swipeable>Hello</Swipeable>
                </div>
                <div className="container" onClick={this.handleClose}>
                <Box
                className="box"
                displayMessage={!open}
                onClick={this.handleOpen}
                pose={open ? "on" : "off"}
                />
            </div>
        </div>
         );
    }
}
 
export default Home;
