import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import { saveAs } from 'file-saver';
import firebase, { auth, provider } from './firebase.js';
import NavbarPage from './Navbar'


class App extends Component {
  constructor() {
    super();
    this.state = {
      name: 'Ujjwal',
      receiptId: 0,
      price1: 0,
      price2: 0,
      user: null 
   }
   this.handleChange = this.handleChange.bind(this);
   this.createAndDownloadPdf1 = this.createAndDownloadPdf1.bind(this);
   this.createAndDownloadPdf2 = this.createAndDownloadPdf2.bind(this);
   this.login = this.login.bind(this);
   this.logout = this.logout.bind(this);
  }
  componentDidMount() {
    auth.onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
      } 
    });}
  logout() {
    // we will add the code for this in a moment, but need to add the method now or the bind will throw an error
    auth.signOut()
    .then(() => {
      this.setState({
        user: null
      });
    });
  }
  login() {
    auth.signInWithPopup(provider) 
      .then((result) => {
        const user = result.user;
        this.setState({
          user
        });
      });
  }
  createAndDownloadPdf1 = () => {
    axios.post('/create-pdf', this.state).then(() => axios.get('/fetch-pdf', { responseType: 'blob' }))
    .then((res) => {const pdfBlob = new Blob([res.data], { type: 'application/pdf' });saveAs(pdfBlob, 'generatedDocument.pdf')})
  }
  createAndDownloadPdf2 = () => {
    axios.post('/create-pdf-1', this.state).then(() => axios.get('/fetch-pdf', { responseType: 'blob' }))
    .then((res) => {const pdfBlob = new Blob([res.data], { type: 'application/pdf' });saveAs(pdfBlob, 'generatedDocument.pdf')})
  }
  handleChange = ({ target: { value, name } }) => this.setState({ [name]: value });
  render() {
    return (
      <div className="App">
      {this.state.user ?
        <button onClick={this.logout}>Log Out</button>                
        :
        <button onClick={this.login}>Log In</button>              
      }
      {this.state.user ?
        <div>
        <input type="text" placeholder="Name" name="name" onChange ={this.handleChange}/>
          <input type="number" placeholder="Receipt ID" name="receiptId"    onChange={this.handleChange}/>
          <input type="number" placeholder="Price 1" name="price1" onChange={this.handleChange}/>
          <input type="number" placeholder="Price 2" name="price2" onChange={this.handleChange}/>
          <button onClick={this.createAndDownloadPdf1}>Download PDF</button>
          <button onClick={this.createAndDownloadPdf2}>Download PDF</button>
        </div>               
        :
        <p>Please log in</p>              
      }

   </div>
    );
  }
}

export default App;
